import { ref } from "vue";
export default {
  name: "ProductoForm",
  props: {
    modelValue: {
      type: Object,
      required: true
    }
  },
  emits: ["update:modelValue"],
  setup(props, {
    emit
  }) {
    const producto = ref({
      ...props.modelValue
    });
    const valido = ref(false);

    // Actualizar prop cuando cambie el local
    watch(producto, newVal => emit("update:modelValue", newVal), {
      deep: true
    });
    return {
      producto,
      valido
    };
  }
};
