// src/stores/productosStore.js
import { defineStore } from "pinia";
import { getProductos, crearProducto, editarProducto, eliminarProducto } from "../services/productos";

export const useProductoStore = defineStore("productos", {
  state: () => ({
    productos: [],
  }),
  actions: {
    async obtenerProductos() {
      try {
        const result = await getProductos();
        this.productos = result.data; // ðŸ”¹ tu API devuelve { data, total, page, pageSize }
      } catch (err) {
        console.error(err);
      }
    },
    async crearProducto(producto) {
      try {
        await crearProducto(producto);
        await this.obtenerProductos();
      } catch (err) {
        console.error(err);
      }
    },
    async editarProducto(producto) {
      try {
        await editarProducto(producto);
        await this.obtenerProductos();
      } catch (err) {
        console.error(err);
      }
    },
    async eliminarProducto(id) {
      try {
        await eliminarProducto(id);
        await this.obtenerProductos();
      } catch (err) {
        console.error(err);
      }
    },
  },
});
