// src/services/productos.js
const API_URL = "http://localhost:3000/api/productos";

export async function getProductos() {
  const res = await fetch(API_URL);
  if (!res.ok) throw new Error("Error al obtener productos");
  return await res.json();
}

export async function getProductoPorId(Id) {
  const res = await fetch(`${API_URL}/${Id}`);
  if (!res.ok) {
    const errorData = await res.json();
    throw new Error(errorData.message || "Producto no encontrado");
  }
  return await res.json();
}

export async function crearProducto(producto) {
  const res = await fetch(API_URL, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(producto),
  });

  if (!res.ok) {
    const errorData = await res.json();
    throw new Error(errorData.message || "Error al crear producto");
  }
  return await res.json();
}

export async function editarProducto(producto) {
  try {
    const res = await fetch(`${API_URL}/${producto.Id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(producto),
    });

    if (!res.ok) {
      const errorData = await res.json();
      throw new Error(errorData.message || "Error al editar producto");
    }

    return await res.json();
  } catch (err) {
    console.error(err);
    throw err;
  }
}

export async function eliminarProducto(Id) {
  const res = await fetch(`${API_URL}/${Id}`, { method: "DELETE" });
  if (!res.ok) {
    const errorData = await res.json();
    throw new Error(errorData.message || "Error al eliminar producto");
  }
  return await res.json();
}
