import { ref } from "vue";
export default {
  name: "ModalConfirm",
  props: {
    mensaje: {
      type: String,
      required: true
    }
  },
  setup(_, {
    emit
  }) {
    const visible = ref(false);
    const abrir = () => {
      visible.value = true;
    };
    const cancelar = () => {
      visible.value = false;
    };
    const confirmar = () => {
      emit("confirmar");
      visible.value = false;
    };
    return {
      visible,
      abrir,
      cancelar,
      confirmar
    };
  }
};
