// src/services/productos.js
const API_URL = "http://localhost:3000/api/productos";

export async function getProductoPorId(id) {
  if (!id) throw new Error("ID de producto no proporcionado");
  const res = await fetch(`${API_URL}/${id}`);
  if (!res.ok) {
    const error = await res.json().catch(() => ({}));
    throw new Error(error.message || "Producto no encontrado");
  }
  return await res.json();
}

export async function editarProducto(producto) {
  if (!producto.Id) throw new Error("El producto debe tener Id para editar");
  const res = await fetch(`${API_URL}/${producto.Id}`, {
    method: "PUT",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(producto),
  });
  if (!res.ok) {
    const error = await res.json().catch(() => ({}));
    throw new Error(error.message || "Error al editar producto");
  }
  return await res.json();
}
