// src/stores/productosStore.js
import { defineStore } from 'pinia'
import { getProductos, getProducto, createProducto, updateProducto, deleteProducto } from '@/api/productos'

export const useProductosStore = defineStore('productos', {
  state: () => ({
    productos: [],
    producto: null,
    loading: false,
    error: null,
  }),

  actions: {
    async fetchProductos() {
      this.loading = true
      try {
        const { data } = await getProductos()
        this.productos = data
      } catch (err) {
        this.error = err
      } finally {
        this.loading = false
      }
    },

    async fetchProducto(id) {
      this.loading = true
      try {
        const { data } = await getProducto(id)
        this.producto = data
      } catch (err) {
        this.error = err
      } finally {
        this.loading = false
      }
    },

    async addProducto(producto) {
      await createProducto(producto)
      await this.fetchProductos()
    },

    async editProducto(id, producto) {
      await updateProducto(id, producto)
      await this.fetchProductos()
    },

    async removeProducto(id) {
      await deleteProducto(id)
      await this.fetchProductos()
    }
  }
})
