import { ref } from "vue";
export default {
  name: "ModalConfirm",
  props: {
    mensaje: String
  },
  setup(_, {
    emit
  }) {
    const visible = ref(false);
    const abrir = () => visible.value = true;
    const confirmar = () => {
      visible.value = false;
      emit("confirmar");
    };
    const cancelar = () => {
      visible.value = false;
      emit("cancelar");
    };
    return {
      visible,
      abrir,
      confirmar,
      cancelar
    };
  }
};
