import { ref } from "vue";
export default {
  name: "ProductoForm",
  props: {
    productoInicial: {
      type: Object,
      default: () => ({
        Nombre: "",
        Sku: "",
        Precio: 0,
        Stock: 0,
        Categoria: "",
        Activo: true
      })
    }
  },
  setup(props, {
    emit
  }) {
    const producto = ref({
      ...props.productoInicial
    });
    const form = ref(null);
    const guardar = () => {
      emit("guardar", producto.value);
    };
    return {
      producto,
      form,
      guardar
    };
  }
};
