import { ref, watch } from "vue";
export default {
  name: "ProductoForm",
  props: {
    productoInicial: {
      type: Object,
      default: () => ({
        Id: null,
        Nombre: "",
        Sku: "",
        Precio: 0,
        Stock: 0,
        Categoria: "",
        Activo: true
      })
    },
    modo: {
      type: String,
      default: "crear" // "crear" o "editar"
    }
  },
  setup(props, {
    emit
  }) {
    const producto = ref({
      ...props.productoInicial
    });
    const form = ref(null);
    const botonTexto = props.modo === "editar" ? "Actualizar" : "Guardar";
    watch(() => props.productoInicial, nuevo => {
      producto.value = {
        ...nuevo
      };
    });
    const guardar = () => {
      emit("guardar", producto.value);
    };
    return {
      producto,
      form,
      guardar,
      botonTexto
    };
  }
};
